import ExerciseComponent from './ExerciseComponent';


function App() {
  //state (état, données)
  const [compteur, setCompteur] = useState(1);
  //comportements
  const handleClick = () => {
    alert("hangleclick done");
  };

  //affichage (render)
  
  return (
  <div>
    <h1>{compteur}</h1>
    <button onClick={handleClick}>methode1</button>
    <button onClick={() => alert("2eme methode branchement composant et affichage")}>methode2</button>
  </div>
  );
  
}

export default App;
